import React ,{useState}from "react";
import { connect } from 'react-redux';
import * as contactAction from '../../actions/contactAction';
import { makeStyles } from "@material-ui/core/styles";
import {
  Typography,
  FormControl,
   Button,
   Input
} from "@material-ui/core";
//import Input from "../../molecules/_input/_input";
//import * as Constants from "../../../constants";
//import Button from "";
import { myTheme } from "../../theme";
const useStyles = makeStyles((theme) => ({
  styleContactMainContent: {
    height: "92.5vh",

    backgroundImage:
      "URL(https://www.xmple.com/wallpaper/gradient-cyan-purple-linear-2736x1824-c2-0bfce2-ee82ee-a-105-f-14.svg)",

    backgroundRepeat: "noRepeat",
    backgroundSize: "cover",
  },
  forms: {
    // border: "1px solid myTheme.palette.secondary.main",
    height: "300PX",
    width: "250px",
    backgroundColor: myTheme.palette.secondary.main,

    color: "black",

    margin: "auto",
    paddingTop: "30px",
    marginTop: "30px",
    paddingLeft: "50px",
  },
  input: {
      paddingBottom: "10px",
  },
  heading: {
    paddingTop: "50px",
  },
  error: {
    color:myTheme.palette.myColor.redColor ,

  },
}));

const ContactMainContent = ({props}) => {
  const classes = useStyles();
  const [name, setName] = useState("");
 const[nameList,setNameList]=useState([]);

  const handleContactChange=(event)=>{
    event.preventDefault();
    const name=(event.target.value);
    setName(name);

  }

  const handleClick = (event) => {
    event.preventDefault();
    let contact = {
        name: name
      }
    props.createContact(contact);
    const list=props.contacts.map((contact, i) => {console.log("abvc",contact.name);return(<div><Typography >{contact.name}</Typography><Button onClick={handleDelete} variant="contained"> Delete</Button></div>)});

setNameList(list)

  }
  const handleDelete=(event,index)=>{
    event.preventDefault();
    props.deleteContact(index);
    console.log(index);
  }

  return (

    <div className={classes.styleContactMainContent} data-testid="contact">
      <Typography variant="h3" className={classes.heading}>
        CONTACT
      </Typography>

      <div className={classes.forms}>
        <form>

          <div className={classes.input}>
              <Typography>Contact</Typography>
            <Input  onChange={handleContactChange}></Input>
          </div>

          <Button onClick={handleClick} variant="contained"> Save</Button>
        </form>

      </div>
{nameList}
    </div>
  );
};

//export default ContactMainContent;
const mapStateToProps = (state, ownProps) => {
    return {
      contacts: state.contacts
    }
  };

  const mapDispatchToProps = (dispatch) => {
    return {
      createContact: contact => dispatch(contactAction.createContact(contact)),
      deleteContact: index =>dispatch(contactAction.deleteContact(index))
    }
  };

  export default connect(mapStateToProps, mapDispatchToProps)(ContactMainContent);
