import React ,{useState}from "react";

import { makeStyles } from "@material-ui/core/styles";
import {
  Typography,
  FormControl,
   Button,
   Input
} from "@material-ui/core";
//import Input from "../../molecules/_input/_input";
//import * as Constants from "../../../constants";
//import Button from "";
import { myTheme } from "../../theme";
const useStyles = makeStyles((theme) => ({
  styleContactMainContent: {
    height: "92.5vh",

    backgroundImage:
      "URL(https://www.xmple.com/wallpaper/gradient-cyan-purple-linear-2736x1824-c2-0bfce2-ee82ee-a-105-f-14.svg)",

    backgroundRepeat: "noRepeat",
    backgroundSize: "cover",
  },
  forms: {
    // border: "1px solid myTheme.palette.secondary.main",
    height: "300PX",
    width: "250px",
    backgroundColor: myTheme.palette.secondary.main,

    color: "black",

    margin: "auto",
    paddingTop: "30px",
    marginTop: "30px",
    paddingLeft: "50px",
  },
  input: {
      paddingBottom: "10px",
  },
  heading: {
    paddingTop: "50px",
  },
  error: {
    color:myTheme.palette.myColor.redColor ,

  },
}));

const ContactMainContent = ({props}) => {
  const classes = useStyles();
  const [name, setName] = useState("");
  const [nameError, setNameError] = useState("");
  const [email, setEmail] = useState("");
  const [emailError, setEmailError] = useState("");
  const [message, setMessage] = useState("");
  const [messageError, setMessageError] = useState("");
  const [error, setError] = useState(true);


  const handleContactChange=(event)=>{
    event.preventDefault();
    const name=(event.target.value);
    setName(name);

  }

  const handleClick = (event) => {
    event.preventDefault();
    let contact = {
        name: name
      }
    props.createContact(contact);


  }
  return (
    <div className={classes.styleContactMainContent} data-testid="contact">
      <Typography variant="h3" className={classes.heading}>
        CONTACT
      </Typography>

      <div className={classes.forms}>
        <form>

          <div className={classes.input}>
              <Typography>Contact</Typography>
            <Input value="" onChange={handleContactChange}></Input>
          </div>

          <Button onClick={handleClick} >Save</Button>
        </form>
      </div>

    </div>
  );
};

export default ContactMainContent;
