import React, { useState } from "react";
import { connect } from "react-redux";
import * as contactAction from "../../../redux/actions/contactAction";
import { makeStyles } from "@material-ui/core/styles";

import {
  Typography,
  FormControl,
  Divider,
  //Input
} from "@material-ui/core";
//import Input from "../../molecules/_input/_input";
//import * as Constants from "../../../constants";
import Button from "../../atoms/_button/_button";
import { myTheme } from "../../../theme";
import Input from "../../atoms/_input/_input";
const useStyles = makeStyles((theme) => ({
  styleContactMainContent: {
    //height: "92.5vh",
    // backgroundImage:
    //   "URL(https://www.xmple.com/wallpaper/gradient-cyan-purple-linear-2736x1824-c2-0bfce2-ee82ee-a-105-f-14.svg)",
    //backgroundRepeat: "noRepeat",
    //backgroundSize: "cover",
  },
  forms: {
    // border: "1px solid myTheme.palette.secondary.main",
    //height: "300PX",
    //width: "250px",
    backgroundColor: myTheme.palette.secondary.main,

    color: "black",

    margin: "auto",
    paddingTop: "30px",
    marginTop: "30px",
    //paddingLeft: "50px",
  },
  input: {
    paddingBottom: "10px",
  },
  heading: {
    paddingTop: "50px",
  },
  error: {
    color: myTheme.palette.myColor.redColor,
  },
}));

const ContactMainContent = ({ props }) => {
  const classes = useStyles();
  // const [name, setName] = useState("");
  // const [email, setEmail] = useState("");
  console.log("list", props.contacts);
  let name="",email="";
  const handleContactChange = (event) => {
    //event.preventDefault();
     name = event.target.value;
    //setName(name);

  };
  const handleEmailChange = (event) => {
    //event.preventDefault();
     email = event.target.value;
    //setEmail(email);
  };

  return (
    <div className={classes.styleContactMainContent} data-testid="contact">
      <div className={classes.forms}>
        <form>
          <div className={classes.input}>
            <Typography variant="h6">Contact Name</Typography>

            <Input onChange={handleContactChange} value=""></Input>
            <Typography variant="h6">Email</Typography>
            <Input onChange={handleEmailChange} value=""></Input>
            <Button
              value="Save"
              action={{
                type: "CREATE_NEW_CONTACT",
                contact: name,
                email: email,
              }}
            >
              {" "}
            </Button>
          </div>
          <Divider></Divider>
        </form>
      </div>
    </div>
  );
};

export default ContactMainContent;
