import React, { Component } from 'react';
import { connect } from 'react-redux';
import * as contactAction from './redux/actions/contactAction';
import Contact from './components/organisms/_contact-form/_contact-form';
import ContactList from "./components/organisms/_contact-list/_contact-list";
import ContactFormPage from "./pages/_contact-form-page/_contact-form.-page";
import { Typography, Input } from "@material-ui/core";
class App extends Component {

  constructor(props){
    super(props);
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);

    this.state = {
      name: ''
    }
  }

  handleChange(e){
    this.setState({
      name: e.target.value
    })
  }

  handleSubmit(e){
    e.preventDefault();
    let contact = {
      name: this.state.name
    }
    this.setState({
      name: ''
    });
    this.props.createContact(contact);
  }

  listView(data, index){
    return (
      <div>

      <div >
        <div >
          <li key={index} className="list-group-item clearfix">
            {data.name}  <button onClick={(e) => this.deleteContact(e, index)} className="btn btn-danger">
            Remove
          </button>
          </li>

        </div>


    </div> 
    </div>
    )
  }

  deleteContact(e, index){
    e.preventDefault();
    this.props.deleteContact(index);
  }

  render() {

    return(
      <div className="container">
        <Typography variant="h1"> Contacts Application</Typography>
        <hr />
        <div>

          <Typography variant="h3">Add Contact Form</Typography>

          {/* <Contact props={this.props} /> 
          <ContactList props={this.props}/>
           */}
           <ContactFormPage props={this.props}> </ContactFormPage>
        </div>
      </div>
    )
  }
}

const mapStateToProps = (state, ownProps) => {
  return {
    contacts: state.contacts
  }
};

const mapDispatchToProps = (dispatch) => {
  return {
    createContact: contact => dispatch(contactAction.createContact(contact)),
    deleteContact: index =>dispatch(contactAction.deleteContact(index))
  }
};

export default connect(mapStateToProps, mapDispatchToProps)(App);
