import * as actionTypes from '../actions/actionTypes';

export default (state = [], action) => {
    switch (action.type){

      case actionTypes.CREATE_NEW_CONTACT:
        console.log("switch");
      return [
        ...state,
        {contact:action.contact,email:action.email}
        // Object.assign({}, action.contact)
      ];
      case actionTypes.REMOVE_CONTACT:
        console.log("io switch",state ,action.id.id);
      return state.filter((data, i) => i !== action.id.id);
      //console.log("io switch",action.id);
      default:
            return state;
    }
  };
